import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001/api',
});

export interface Link {
  id: string;
  url: string;
  title: string;
  description?: string;
  createdAt: string;
  updatedAt: string;
}

export interface CreateLinkData {
  url: string;
  title: string;
  description?: string;
}

export interface UpdateLinkData extends Partial<CreateLinkData> {}

export const linksApi = {
  getAll: () => api.get<Link[]>('/links'),
  getById: (id: string) => api.get<Link>(`/links/${id}`),
  create: (data: CreateLinkData) => api.post<Link>('/links', data),
  update: (id: string, data: UpdateLinkData) => api.put<Link>(`/links/${id}`, data),
  delete: (id: string) => api.delete(`/links/${id}`),
}; 